#!/bin/bash btisoname=bt4.iso btmodisoname=`basename $btisoname .iso`-mod.iso luks_image=0 count=2560000 clear echo &#34;##############################################################&#34; echo &#34;[*] BackTrack 4 customisation script&#34; echo &#34;[*] Setting up the build environment...&#34; services=&#34;inetutils-inetd tinyproxy iodined knockd openvpn atftpd ntop nstxd nstxcd apache2 sendmail atd dhcp3-server winbind miredo miredo-server pcscd wicd wacom cups bluetooth binfmt-support mysql vmware vboxdrv kvm nessusd&#34; test -d mnt || mkdir -p mnt test -d extract-cd || mkdir -p extract-cd test -d squashfs || mkdir -p squashfs test -d container || mkdir -p container mount -o loop,ro $btisoname mnt/ rsync --exclude=/casper/filesystem.
